const validation = (userData) => {
  let errors = {};

  if (!/\S+@\S+\.\S+/.test(userData.email)) {
    errors.email = "Enter a valid email";
  }
  if (!userData.email) {
    errors.email = "Email can not be null ";
  }
  if (userData.email.length > 35) {
    errors.email = "Email length limit is 35";
  }

  if (!userData.password.match(/\d/)) {
    errors.password = "Password must have a number";
  }
  if (userData.password.length < 6 || userData.password.length > 10) {
    errors.password = "Password must be between 6 and 10 digits";
  }

  return errors;
};

function isButtonDisabled(errors, input) {
  return (
    Object.values(errors).some((value) => value === true) ||
    !input.name ||
    !input.lastName ||
    !input.email ||
    !input.password
  );
}

export { validation, isButtonDisabled };
