import style from "./Home.module.css";
import CardContainer from "../CardContainer/CardContainer";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react"; // Import useState
import { getCharacter } from "../../redux/actions";

const Home = () => {
  const dispatch = useDispatch();
  const characters = useSelector((state) => state.characters);
  const [isLoading, setIsLoading] = useState(true); // Add isLoading state

  useEffect(() => {
    if (characters.length < 1) {
      dispatch(getCharacter())
        .then(() => setIsLoading(false)) // Set isLoading to false when data is fetched
        .catch((error) => {
          // Handle error here if needed
          console.error(error);
        });
    } else {
      setIsLoading(false); // If characters are already available, set isLoading to false
    }
  }, [dispatch, characters]);

  if (isLoading) {
    return <h2 className={style.loading}>LOADING ...</h2>;
  } else {
    return (
      <div>
        <CardContainer characters={characters} />
      </div>
    );
  }
};

export default Home;
