import {
  GET_CHARACTERS,
  GET_BY_NAME,
  GET_BY_ID,
  SIGNUP,
  LOGIN,
  POST_CHARACTER,
  DELETE_CHARACTER,
} from "./actions";

const initialState = {
  characters: [],
  users: [],
  user: [],
  detail: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_CHARACTERS:
      return {
        ...state,
        characters: action.payload,
      };
    case GET_BY_NAME:
      return {
        ...state,
        characters: action.payload,
      };
    case GET_BY_ID:
      return {
        ...state,
        detail: action.payload,
      };
    case POST_CHARACTER:
      return {
        ...state,
      };
    case DELETE_CHARACTER:
      return {
        ...state,
        characters: action.payload,
      };
    case SIGNUP:
      return {
        ...state,
        users: action.payload,
      };
    case LOGIN:
      return {
        ...state,
        user: action.payload,
      };
    default:
      return { ...state };
  }
};

export default reducer;
