import style from "./Nav.module.css";
import Container from "react-bootstrap/Container";
import Navs from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import logoNav from "../../resources/logoNav.png";
import { Button, Image } from "react-bootstrap";
import { Link } from "react-router-dom";
import { Link as ScrollLink } from "react-scroll";

const Nav = () => {
  return (
    <Navbar fixed="top" bg="white" variant="light" className={style.nav}>
      <Container>
        <Navbar.Brand href="/">
          <Image className={style.logo} src={logoNav} />
        </Navbar.Brand>
        <Navbar.Collapse id="basic-navbar-nav">
          <Navs className="me-auto" style={{ marginTop: "20px" }}>
            {/* Utiliza ScrollLink en lugar de Nav.Link para lograr el desplazamiento suave */}
            <ScrollLink
              to="por-que-zucca"
              smooth={true}
              duration={500}
              className="nav-link mb-3"
            >
              ¿Por qué Zucca?
            </ScrollLink>
            <ScrollLink
              to="quienes-nos-eligen"
              smooth={true}
              duration={500}
              className="nav-link mb-3"
            >
              Quienes nos eligen
            </ScrollLink>
            <ScrollLink
              to="como-trabajamos"
              smooth={true}
              duration={500}
              className="nav-link mb-3"
            >
              ¿Cómo trabajamos?
            </ScrollLink>
          </Navs>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Nav;
