import style from "./Home.module.css";
import CardContainer from "../CardContainer/CardContainer";
import Paginate from "../Paginate/Paginate";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { getCharacter, deleteCharacter } from "../../redux/actions";

const Home = () => {
  const dispatch = useDispatch();
  const characters = useSelector((state) => state.characters);

  const [currentPage, setCurrentPage] = useState(0);
  const itemsPerPage = 8;

  const offset = currentPage * itemsPerPage;
  const currentItems = characters.slice(offset, offset + itemsPerPage);
  const pageCount = Math.ceil(characters.length / itemsPerPage);

  useEffect(() => {
    if (characters.length < 1) dispatch(getCharacter());
  }, [dispatch, characters]);

  const handlePageChange = ({ selected }) => {
    setCurrentPage(selected);
  };

  const onClose = (id) => {
    dispatch(deleteCharacter(id));
  };

  if (characters.length) {
    return (
      <div>
        <CardContainer onClose={onClose} characters={currentItems} />
        <Paginate pageCount={pageCount} onPageChange={handlePageChange} />
      </div>
    );
  } else {
    return <h2 className={style.loading}>LOADING ...</h2>;
  }
};

export default Home;
