import { useState } from "react";
import validation from "./validations";

const Form = ({login}) => {

    const [userData, setData] = useState({
        email:'',password:''
    })


    const [errors, setErrors] = useState({})


    const handleChange = (event) => {
        setData({
            ...userData, [event.target.name] : event.target.value
        })

        setErrors(validation({
            ...userData, [event.target.name] : event.target.value
        }))
    }

    const handleSubmit = (event) => {
            event.preventDefault()
            login(userData)
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label htmlFor="email">Email:</label>
                <input name="email" type="email" placeholder="ingrese su mail" value={userData.email} onChange={handleChange}></input>
                {errors.email && <p>{errors.email}</p>}
                <hr/>
                <label htmlFor="password">Password:</label>
                <input name="password" type="password" placeholder="ingrese una password" value={userData.password} onChange={handleChange}></input>
                {errors.password && <p>{errors.password}</p>}
                <hr/>
                <button>Submit</button>
            </form>
        </div>
    )
}

export default Form
