import { ADD_FAV,REMOVE_FAV } from "./action-types";

const initialState = {
    myFavorites:[]
    }

const reducer = (state = initialState,action) => {
    switch(action.type){
        case ADD_FAV:
            return{
                ...state,
                myFavorites:[...state.myFavorites,action.payload]

            }
        case REMOVE_FAV:
            let filteredlist = state.myFavorites.filter((favorite)=>favorite.id !== action.payload)
            return{
                ...state,
                myFavorites:filteredlist

            }
        default:
            return{...state}
    }   
}

export default reducer
