import style from "./Card.module.css";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

const Card = ({ character }) => {
  const { id, name, species, gender, image } = character;
  return (
    <div className={style.divCard}>
      <div className={style.btnCards}>
        <div>
          <button onClick={handleFavorite} className={style.btnFav}>
            ❤️
          </button>
        </div>

        <div>
          <button onClick={onClose} className={style.btn}>
            X
          </button>
        </div>
      </div>
      <div className={style.divInfo}>
        <img src={image} alt={name} className={style.image} />
        <Link to={`/detail/${id}`} className={style.link}>
          <h2 className={style.name}>{name}</h2>
        </Link>

        <div className={style.divSpeciesGender}>
          <h2 className={style.species}>{species}</h2>
          <h2 className={style.gender}>{gender}</h2>
        </div>
      </div>
    </div>
  );
};

export default Card;
