import style from "./Card.module.css";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  favCharacter,
  unfavCharacter,
  getFavorites,
} from "../../redux/actions";

const Card = ({ name, species, gender, image, onClose, id }) => {
  const [isFav, setIsFav] = useState(false);
  const dispatch = useDispatch();
  const favorites = useSelector((state) => state.favorites);

  console.log(favorites);

  useEffect(() => {
    const user = JSON.parse(window.localStorage.getItem("user"));
    const id = user.id;
    dispatch(getFavorites(id));
  }, []);

  const onFav = () => {
    if (isFav) {
      setIsFav(false);
      dispatch(unfavCharacter(id));
    } else {
      setIsFav(true);
      dispatch(favCharacter(id));
    }
  };

  useEffect(() => {
    favorites.forEach((fav) => {
      if (fav.id === id) {
        setIsFav(true);
      }
    });
  }, [favorites]);

  return (
    <div className={style.divCard}>
      <div className={style.btnCards}>
        <div>
          {isFav ? (
            <button onClick={onFav} className={style.btnFav}>
              ‚ù§Ô∏è
            </button>
          ) : (
            <button onClick={onFav} className={style.btnFav}>
              ü§ç
            </button>
          )}
        </div>

        <div>
          <button onClick={onClose} className={style.btn}>
            X
          </button>
        </div>
      </div>
      <div className={style.divInfo}>
        <img src={image} alt={name} className={style.image} />
        <Link to={`/detail/${id}`} className={style.link}>
          <h2 className={style.name}>{name}</h2>
        </Link>

        <div className={style.divSpeciesGender}>
          <h2 className={style.species}>{species}</h2>
          <h2 className={style.gender}>{gender}</h2>
        </div>
      </div>
    </div>
  );
};

export default Card;
