import style from "./Home.module.css";
import CardContainer from "../CardContainer/CardContainer";
import BootstrapPagination from "../Paginate/Paginate";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { getCharacter, deleteCharacter } from "../../redux/actions";

const Home = () => {
  const dispatch = useDispatch();
  const characters = useSelector((state) => state.characters);

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 8;

  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentItems = characters.slice(startIndex, endIndex);
  const totalPages = Math.ceil(characters.length / itemsPerPage);

  useEffect(() => {
    if (characters.length < 1) dispatch(getCharacter());
  }, [dispatch, characters]);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const onClose = (id) => {
    dispatch(deleteCharacter(id));
  };

  if (characters.length) {
    return (
      <div className={style.home}>
        <CardContainer onClose={onClose} characters={currentItems} />
        <BootstrapPagination
          activePage={currentPage}
          totalPages={totalPages}
          onPageChange={handlePageChange}
        />
      </div>
    );
  } else {
    return <h2 className={style.loading}>LOADING ...</h2>;
  }
};

export default Home;
